import React, { useState } from 'react';
import ActiveOrders from './components/ActiveOrders';
import MenuEditor from './components/MenuEditor';

const mockOrders = [
  { tableId: 1, items: ['Burger', 'Cola'], total: 120.5, status: 'Preparing' },
  { tableId: 2, items: ['Pizza', 'Ayran'], total: 200.0, status: 'New' },
];
const mockMenu = [
  { id: 1, name: 'Burger', price: 90 },
  { id: 2, name: 'Pizza', price: 150 },
];

export default function App() {
  const [view, setView] = useState('orders');
  const [orders, setOrders] = useState(mockOrders);
  const [menu, setMenu] = useState(mockMenu);

  const updateOrderStatus = (tableId, newStatus) =>
    setOrders(prev => prev.map(o => o.tableId === tableId ? { ...o, status: newStatus } : o));

  const saveMenu = items => setMenu(items);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <header className="flex items-center justify-between mb-8">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <div className="space-x-4">
          <button
            onClick={() => setView('orders')}
            className={`px-4 py-2 rounded ${view==='orders' ? 'bg-blue-600 text-white' : 'bg-white border'}`}
          >Active Orders</button>
          <button
            onClick={() => setView('menu')}
            className={`px-4 py-2 rounded ${view==='menu' ? 'bg-blue-600 text-white' : 'bg-white border'}`}
          >Menu Editor</button>
        </div>
      </header>
      {view==='orders' ? 
        <ActiveOrders orders={orders} onUpdateStatus={updateOrderStatus}/> :
        <MenuEditor menu={menu} onSave={saveMenu}/>
      }
    </div>
);
}
