import React, { useState } from 'react';

export default function MenuEditor({ menu, onSave }) {
  const [items, setItems] = useState(menu);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');

  const add = () => {
    if(!name||!price) return;
    setItems(prev => [...prev, { id: Date.now(), name, price: parseFloat(price) }]);
    setName(''); setPrice('');
  };
  const remove = id => setItems(prev => prev.filter(item => item.id!==id));
  return (
    <div className="space-y-6">
      <div className="bg-white p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Add New Item</h2>
        <div className="flex gap-2">
          <input value={name} onChange={e=>setName(e.target.value)} placeholder="Name" className="border p-2 rounded flex-1"/>
          <input value={price} onChange={e=>setPrice(e.target.value)} placeholder="Price" type="number" className="border p-2 rounded w-24"/>
          <button onClick={add} className="px-3 py-1 bg-blue-600 text-white rounded">Add</button>
        </div>
      </div>
      <div className="grid gap-4">
        {items.map(item=>(
          <div key={item.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
            <span>{item.name} — {item.price.toFixed(2)} ₺</span>
            <button onClick={()=>remove(item.id)} className="px-2 py-1 bg-red-600 text-white rounded">Remove</button>
          </div>
        ))}
      </div>
      <div className="text-right">
        <button onClick={()=>onSave(items)} className="px-4 py-2 bg-green-600 text-white rounded">Save Menu</button>
      </div>
    </div>
);
}
